import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/styles.css';

function MenuPage() {
    const [cart, setCart] = useState([]);
    const navigate = useNavigate();
    
    
    const pizzaData = [
        { 
            id: 1, 
            name: 'Tandoori Paneer Pizza', 
            price: 299, 
            ingredients: 'Paneer, Tandoori Sauce, Bell Peppers', 
            image: 'https://assets.grok.com/users/ccc56ea2-6ceb-45e5-b6f0-ff8545941d97/generated/sCWKK423ruOFLL3P/image.jpg'
        },
        { 
            id: 2, 
            name: 'Masala Chicken Pizza', 
            price: 399, 
            ingredients: 'Spicy Chicken Tikka, Onions, Capsicum', 
            image: 'https://images.unsplash.com/photo-1565299624946-b28f40a0ae38?w=500&auto=format&fit=crop&q=60'
        },
        { 
            id: 3, 
            name: 'Vegetable Makhani Pizza', 
            price: 349, 
            ingredients: 'Makhani Sauce, Mixed Vegetables', 
            image: 'https://images.unsplash.com/photo-1565299624946-b28f40a0ae38?w=500&auto=format&fit=crop&q=60'
        },
        { 
            id: 4, 
            name: 'Butter Chicken Pizza', 
            price: 499, 
            ingredients: 'Butter Chicken Chunks, Cream, Spices', 
            image: 'https://assets.grok.com/users/ccc56ea2-6ceb-45e5-b6f0-ff8545941d97/generated/Tq0O9RC0N19D9saG/image.jpg'
        }
    ];

    const addToCart = (pizza) => {
        const existingItem = cart.find(item => item.id === pizza.id);
        if (existingItem) {
            setCart(cart.map(item => 
                item.id === pizza.id 
                ? {...item, quantity: item.quantity + 1} 
                : item
            ));
        } else {
            setCart([...cart, {...pizza, quantity: 1}]);
        }
    };

    const calculateTotal = () => {
        return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
    };

    const proceedToCheckout = () => {
        localStorage.setItem('cart', JSON.stringify(cart));
        navigate('/order');
    };

    return (
        <div className="menu-page">
            <header className="menu-header">
                <div className="logo-container">
                    <img 
                        src="https://imgs.search.brave.com/dHzkjoE1mj5hrXtJhGOfrlDARhzy9y7PqqeKq-Fr7QU/rs:fit:500:0:0:0/g:ce/aHR0cHM6Ly90NC5m/dGNkbi5uZXQvanBn/LzAyLzExLzU1LzE3/LzM2MF9GXzIxMTU1/MTcxOF9PbDdlT1FZ/TkRLNVM4cGJFSE1r/YWdrOWtiZFlUSjJp/WC5qcGc"
                        alt="PizzaExpress Logo" 
                        className="logo"
                    />
                    <span>PizzaExpress</span>
                </div>
                <div className="cart-icon-container">
                    <span className="cart-icon">🛒</span>
                    <span className="cart-count">{cart.length}</span>
                </div>
            </header>

            <div className="menu-content">
                <div className="pizza-grid">
                    {pizzaData.map(pizza => (
                        <div 
                            key={pizza.id} 
                            className="pizza-card"
                        >
                            <img 
                                src={pizza.image} 
                                alt={pizza.name} 
                                className="pizza-image"
                                onError={(e) => {
                                    e.target.onerror = null; 
                                    e.target.src = 'https://via.placeholder.com/300x200?text=Pizza+Image';
                                }}
                            />
                            <div className="pizza-info">
                                <h2 className="pizza-name">{pizza.name}</h2>
                                <p className="pizza-ingredients">{pizza.ingredients}</p>
                                <div className="pizza-price-cart">
                                    <span className="pizza-price">₹{pizza.price}</span>
                                    <button 
                                        onClick={() => addToCart(pizza)}
                                        className="add-to-cart-btn"
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                {cart.length > 0 && (
                    <div className="cart-footer">
                        <div className="cart-footer-container">
                            <div>
                                <span className="cart-total">Total: ₹{calculateTotal()}</span>
                                <span className="cart-items-count">
                                    {cart.reduce((total, item) => total + item.quantity, 0)} items
                                </span>
                            </div>
                            <button 
                                onClick={proceedToCheckout}
                                className="checkout-btn"
                            >
                                Proceed to Checkout
                            </button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default MenuPage;
